svg.script:
// get reference to circle
var circle = document.documentElement.getElementById('circle1');
var cx = 200;

function doAnimationFrame() {
  cx += 10;
  circle.setAttribute('cx', cx);

  if (cx > 400) {
    cx = 0;
  }

  // continue animation
  window.requestAnimationFrame(doAnimationFrame);
}

// start animation
window.requestAnimationFrame(doAnimationFrame);
